import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import api from '../api'; // Import your api instance here

const NavbarContainer = styled.nav`
  background-color: darkgreen;
  color: #ffffff;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem;
`;

const LogoLink = styled(Link)`
  font-family: 'Pacifico', cursive;
  font-size: 2rem;
  text-decoration: none;
  color: yellow;
  margin: 0 auto;
  display: flex;
  align-items: center;
`;

const LogoIcon = styled.img`
  width: 80px; /* Adjust the size as needed */
  height: 80px; /* Adjust the size as needed */
  margin-right: 0.5rem;
`;

const UserContainer = styled.div`
  display: flex;
  align-items: center;
`;

const ProfilePic = styled.img`
  width: 30px;
  height: 30px;
  border-radius: 50%;
  margin-right: 0.5rem;
`;

const Username = styled.span`
  font-weight: bold;
`;

const Navbar = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Fetch user data using the api instance
    api.get('/api/auth/:id') // Replace '123' with the actual user ID
      .then(response => {
        setUser(response.data);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  return (
    <NavbarContainer>
      <LogoLink to="/">
        <LogoIcon src="/path/to/logo.svg" alt="Logo" />
        DopaGram
      </LogoLink>
      {user && (
        <UserContainer>
          <ProfilePic src={user.profilePicture} alt="User Profile" />
          <Username>{user.username}</Username>
        </UserContainer>
      )}
    </NavbarContainer>
  );
};

export default Navbar;
