import React, { useState, useEffect } from 'react';
import axios from '../api'; // Import your Axios instance
import Navbar from './Navbar'; // Import the Navbar component

function UserProfile({ userId }) {
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetchUserProfile();
    fetchUserPosts();
  }, [userId]);

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get(`/api/auth/${userId}/:id`); // Replace with your API endpoint for fetching user profile
      setUser(response.data);
    } catch (error) {
      console.error('Error fetching user profile:', error);
    }
  };

  const fetchUserPosts = async () => {
    try {
      const response = await axios.get(`/api/users/${userId}/getallposts`); // Replace with your API endpoint for fetching user posts
      setPosts(response.data);
    } catch (error) {
      console.error('Error fetching user posts:', error);
    }
  };

  return (
    <div>
      <Navbar /> {/* Include the Navbar component */}
      {user ? (
        <div className="user-profile">
          <div className="profile-header">
            <img className="profile-image" src={user.profileImage} alt={user.username} />
            <div className="profile-info">
              <h1>{user.username}</h1>
              <p>{user.bio}</p>
            </div>
          </div>
          <div className="user-posts">
            {posts.map((post) => (
              <div key={post._id} className="post">
                <div className="post-content">{post.content}</div>
              </div>
            ))}
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
      <style>
        {`
          /* CSS styles for the user profile */
          .user-profile {
            background-color: #f5f5f5;
            padding: 20px;
            display: flex;
            flex-direction: column;
            align-items: center;
          }

          .profile-header {
            display: flex;
            align-items: center;
            margin-bottom: 20px;
          }

          .profile-image {
            width: 100px;
            height: 100px;
            border-radius: 50%;
            margin-right: 20px;
          }

          .profile-info {
            display: flex;
            flex-direction: column;
          }

          .profile-info h1 {
            font-size: 1.5rem;
            margin: 0;
            color: #333;
          }

          .profile-info p {
            font-size: 1rem;
            color: #555;
            margin: 5px 0 0 0;
          }

          .user-posts {
            width: 100%;
            max-width: 600px;
          }

          .post {
            background-color: #fff;
            padding: 10px;
            border: 1px solid #ddd;
            border-radius: 5px;
            margin-top: 10px;
          }

          .post-content {
            font-size: 1rem;
            color: #333;
          }
        `}
      </style>
    </div>
  );
}

export default UserProfile;
