// CSS Styles (same as before)
const userprofileStyles = `
.user-profile {
    background-color: #f5f5f5;
    padding: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
  }

  .profile-header {
    display: flex;
    align-items: center;
    margin-bottom: 20px;
  }

  .profile-image {
    width: 100px;
    height: 100px;
    border-radius: 50%;
    margin-right: 20px;
  }

  .profile-info {
    display: flex;
    flex-direction: column;
  }

  .profile-info h1 {
    font-size: 1.5rem;
    margin: 0;
    color: #333;
  }

  .profile-info p {
    font-size: 1rem;
    color: #555;
    margin: 5px 0 
`;

// Create a style element and append the CSS to the head (same as before)
const styleElement = document.createElement('style');
styleElement.innerHTML = userprofileStyles;
document.head.appendChild(styleElement);

export default Comment;
