import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import api from '../api'; // Import your api instance here

const NavbarContainer = styled.nav`
  background-color: darkgreen;
  color: #ffffff;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem;
`;

const LogoLink = styled(Link)`
  color: black;
  font-size: 3.5rem;
  text-decoration: none;
  display: flex;
  justify-content: center;
`;

const UserContainer = styled.div`
  display: flex;
  align-items: center;
`;

const ProfilePic = styled.img`
  width: 30px;
  height: 30px;
  border-radius: 50%;
  margin-right: 0.5rem;
`;

const Username = styled.span`
  font-weight: bold;
`;

const Navbar = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Fetch user data using the api instance
    api.get('/api/auth/:id') // Replace '123' with the actual user ID
      .then(response => {
        setUser(response.data);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  return (
    <NavbarContainer>
      <div>
        <LogoLink to="/">DopaGram</LogoLink>
      </div>
      {user && (
        <UserContainer>
          <ProfilePic src={user.profilePicture} alt="User Profile" />
          <Username>{user.username}</Username>
        </UserContainer>
      )}
    </NavbarContainer>
  );
};

export default Navbar;
