import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from '../api';

function Navbar() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    try {
      const response = await axios.get('/api/auth/:id'); // Assuming this endpoint returns user data
      setUser(response.data);
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  const navbarStyles = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: 'darkgreen',
    padding: '0.5rem'
  };

  const logoStyles = {
    fontSize: '1.5rem',
    color: '#fff',
    textDecoration: 'none',
    display: 'flex',
    alignItems: 'center',
    fontFamily: 'Arial, sans-serif',
    fontWeight: 'bold',
    letterSpacing: '2px'
  };

  const logoTextStyles = {
    marginRight: '0.5rem'
};

  const navLinkStyles = {
    listStyle: 'none',
    display: 'flex',
    gap: '1.5rem',
    fontSize: '1rem'
  };

  const linkStyles = {
    color: 'Yellow',
    textDecoration: 'none',
  };

  const userInfoStyles = {
    fontSize: '1rem',
    fontWeight: 'bold'
  };

  return (
    <nav style={navbarStyles}>
      <div style={logoStyles}>
        <div style = {logoTextStyles}><Link to="/">DopaGram</Link></div>
      </div>
      <ul style={navLinkStyles}>
        <li><Link to="/" style={linkStyles}>Home</Link></li>
        <li><Link to="/profile" style={linkStyles}>Profile</Link></li>
        <li><Link to="/login" style={linkStyles}>Login</Link></li>
        <li><Link to="/register" style={linkStyles}>Register</Link></li>
      </ul>
      {user && (
        <div style={userInfoStyles}>
          Welcome, {user.username}!
        </div>
      )}
    </nav>
  );
}

export default Navbar;
